content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  let { rating, e } = req?.body;
  console.log(rating);
  let res = "Failure";
  if (e === "Create") {
    const createrating = await db.rating.create({
      data: { ...rating },
    }); //insert database
    res = "Insert Success";
  }
  if (e === "Update") {
    const updaterating = await db.rating.update({
      where: {
        rating_id: rating.rating_id,
      },
      data: { ...rating },
    });
    // console.log(updateUser);
    rating = updaterating;
    res = "Update Success";
  }
  if (e === "ReadAll") {
    const readrating = await db.rating.findMany({
      orderBy: {
        rating_id: "asc",
      },
    });
    rating = readrating;
    // console.log(rating);
    res = "Read rating Success";
  }
  if (e === "ReadById") {
    const readrating = await db.rating.findFirst({
      where: {
        rating_id: rating.rating_id,
      },
    });
    rating = readrating;
    // console.log(user.rating.rating_name);
    res = rating ? "Read rating Success" : "rating not found";
  }
  if (e === "ReadByBookId") {
    const readrating = await db.rating.findMany({
      where: {
        rating_book_id: rating.rating_book_id,
      },
    });
    rating = readrating;
    // console.log(user.rating.rating_name);
    res = rating ? "Read rating Success" : "rating not found";
  }
  if (e === "ReadByUserId") {
    const readrating = await db.rating.findMany({
      where: {
        rating_user_id: rating.rating_user_id,
      },
      include: {
        books: {
          include: {
            authors_authorsTobooks_book_author_one_id: true,
            authors_authorsTobooks_book_author_two_id: true,
            authors_authorsTobooks_book_author_three_id: true,
            tags_books_book_genre_one_idTotags: true,
            tags_books_book_genre_two_idTotags: true,
            tags_books_book_genre_three_idTotags: true,
          },
        },
      },
      orderBy: {
        rating_date_updated: "desc",
      },
    });
    rating = readrating;
    // console.log(user.rating.rating_name);
    res = rating ? "Read rating Success" : "rating not found";
  }
  if (e === "ReadByBooknUserId") {
    const readrating = await db.rating.findFirst({
      where: {
        rating_user_id: rating.rating_user_id,
        rating_book_id: rating.rating_book_id,
      },
    });
    rating = readrating;
    // console.log(user.rating.rating_name);
    res = rating ? "Read rating Success" : "rating not found";
  }
  if (e === "Delete") {
    try {
      rating = await db.rating.delete({
        where: {
          rating_id: rating.rating_id,
        },
      });
      res = "Delete Success";
    } catch (e) {
      console.log(e);
    }
  }
  if (e === "AverageScore") {
    const readrating = await db.rating.aggregate({
      _avg: {
        rating_point: true,
      },
      where: {
        rating_book_id: rating.book_id,
      },
    });
    rating = readrating;
    res = rating ? "Read rating Success" : "rating not found";
  }
  reply.send({
    res: res,
    rating,
    e,
  })
}
