content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { data, event } = req.body;
  const username = "admin";
  const password = "admin";
  let res = "Password Salah";
  let isTrue = false;
  if (event === "Login") {
    if (data.username === username) {
      if (data.password === password) {
        res = "Login Success";
        isTrue = true;
        user.user = {
          role: user.user.role,
          roleAdmin: "admin",
        };
      } else {
        res = "Password wrong";
      }
    } else {
      res = "Username wrong";
    }
  }
  if (event === "Logout") {
    res = "Logout Success";
    isTrue = true;
    user.user = {
      role: user.user.role,
      roleAdmin: "guest",
    };
  }
  reply.send({
    res: res,
    value: isTrue,
  })
}
