async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  // console.log(req.body);
  // const users = req.body;
  let { users, e } = req?.body;
  let res = "Failure";
  if (e === "Register") {
    const createUser = await db.users.create({ data: users }); //insert database
    res = "Register Success";
  }
  if (e === "Login") {
    const loggedUser = await db.users.findFirst({
      where: {
        user_email: users.user_email,
        user_password: users.user_password,
      },
    });
    users = { ...loggedUser };
    user.user = { role: "users", user: { ...loggedUser } };
    res = "Login Success";
  }
  if (e === "Update") {
    const updateUser = await db.users.update({
      where: {
        user_id: users.user_id,
      },
      data: users,
    });
    // console.log(updateUser);
    res = "Update Success";
    users = updateUser;
  }
  if (e === "ReadMany") {
    users = await db.users.findMany({});
    res = "Read All Success";
  }
  if (e === "Logout") {
    user.user = { role: "guest" };
    res = "User Logout";
  }
  // console.log(users);
  reply.send({
    res: res,
    e,
    users,
  })
}
