content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  // console.log(req.body);
  const auser = req.body;
  const loggedUser = await db.users.findFirst({
    where: {
      user_email: auser.user_email,
      user_password: auser.user_password,
    },
  });

  reply.send({
    res: loggedUser,
  })
}
