content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  let { book, e } = req?.body;

  let res = "Failure";
  if (e === "Create") {
    const createbook = await db.books.create({
      data: { ...book },
    });
    res = "Insert Success";
  }
  if (e === "Update") {
    const updatebook = await db.books.update({
      where: {
        book_id: book.book_id,
      },
      data: { ...book },
    });
    book = updatebook;
    res = "Update Success";
  }
  if (e === "ReadAll") {
    const readbook = await db.books.findMany({
      orderBy: {
        book_id: "asc",
      },
    });
    book = readbook;
    res = "Read Success";
  }
  if (e === "ReadById") {
    const readbook = await db.books.findFirst({
      where: {
        book_id: book.book_id,
      },
      include: {
        category: true,
      },
    });
    book = readbook;
    res = book ? "Read Success" : "book not found";
  }
  if (e === "Delete") {
    try {
      book = await db.books.delete({
        where: {
          book_id: book.book_id,
        },
      });
      res = "Delete Success";
    } catch (e) {
      console.log(e);
    }
  }
  if (e === "ReadDetailById") {
    const readbook = await db.books.findFirst({
      where: {
        book_id: book.book_id,
      },
      include: {
        category: true,
        publisher: true,
        chapter: true,
        page: true,
        authors_authorsTobooks_book_author_one_id: true,
        authors_authorsTobooks_book_author_two_id: true,
        authors_authorsTobooks_book_author_three_id: true,
        tags_books_book_genre_one_idTotags: true,
        tags_books_book_genre_two_idTotags: true,
        tags_books_book_genre_three_idTotags: true,
      },
    });
    book = readbook;
    res = book ? "Read Success" : "book not found";
  }
  reply.send({
    res: res,
    book,
    e,
  })
}
