async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { nama } = req.query;
  const { username, password } = req.body;
  const en = await ext.encrypt("12345");
  const de = await ext.decrypt(en);
  reply.send({
    hello: "world",
    username,
    password: en,
    "password-decrypt": de,
    nama,
  })
}
