async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  let { authors, e } = req?.body;
  console.log(authors);
  let res = "Failure";
  if (e === "Create") {
    const createAuthor = await db.authors.create({
      data: authors,
    }); //insert database
    res = "Insert Author Success";
  }
  if (e === "Update") {
    const updateAuthor = await db.authors.update({
      where: {
        author_id: authors.user_id,
      },
      data: authors,
    });
    // console.log(updateUser);
    authors = updateAuthor;
    res = "Update Author Success";
  }
  if (e === "ReadAll") {
    const readAuthors = await db.authors.findMany();
    authors = readAuthors;
    user = {
      role: "author",
      author: authors,
    };
    console.log(user);
    res = "Read Authors Success";
  }
  reply.send({
    res: res,
    authors,
    e,
  })
}
